public class Solution2 {

    public boolean repeatedSubstringPattern(String s) {
        //如果是某个子串重复的话 一定在 s+s中间能找到重复的s
        return (s+s).substring(1,s.length()*2-1).indexOf(s)!=-1;
    }
    public static void main(String[] args) {
//        System.out.println(new Solution2().repeatedSubstringPattern("abaababaab"));
//        System.out.println(new practice485().repeatedSubstringPattern("abaaba"));
        System.out.println(new Solution2().repeatedSubstringPattern(""));
    }
}
